        accessKey: "someKey",
        secretKey: "******",
        type: "S3BucketAuthentication",
    },
    category: "aws/elb",
    collectorId: collector.id,
    contentType: "AwsElbBucket",
    description: "My description",
    path: {
        bucketName: "Bucket1",
        pathExpression: "*",
        type: "S3BucketPathExpression",
    },
    paused: false,
    scanInterval: 300000,
});
